<?php require_once '../config.php'; require_admin(); // simple CSV header('Content-Type: text/csv'); header('Content-Disposition: attachment; filename="epss_assessments.csv"'); $out = fopen('php://output', 'w'); // header row fputcsv($out, ['Assessment ID', 'User', 'Region', 'Question', 'Answer', 'Submitted At']); $stmt = $pdo->query(" SELECT a.id, u.name AS user_name, u.region, q.question_text, a.answer, a.submitted_at FROM assessments a JOIN users u ON a.user_id = u.id JOIN questions q ON a.question_id = q.id ORDER BY a.submitted_at DESC "); while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) { fputcsv($out, [ $row['id'], $row['user_name'], $row['region'], $row['question_text'], $row['answer'], $row['submitted_at'] ]); } fclose($out); exit; 